import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

class BookUpdate extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            _id: this.props.book?._id,
            title: this.props.book?.name,
            description: this.props.book?.color,
            hasClaws: this.props.book?.hasClaws
        }
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log("this.state: ", this.state)
        this.props.handleBookUpdate(this.state);
    }

    handleTitleChange = event => this.setState({title: event.target.value});
    handleDescriptionChange = event => this.setState({description: event.target.value});
    handleStatusChange = event => this.setState({status: event.target.value});

    render() {
        return (
            <Modal show={this.props.show} onHide={this.props.onHide} >
                <Modal.Dialog>
                    <Modal.Header closeButton>
                        <Modal.Title>Update your book!</Modal.Title>
                    </Modal.Header>

                    <Modal.Body>
                        <Form onSubmit={this.handleSubmit} >
                            <Form.Group className="mb-3" controlId="formTitle">
                                <Form.Label>Title</Form.Label>
                                <Form.Control 
                                    type="name" 
                                    placeholder={this.state.title}
                                    onChange={this.handleTitleChange}
                                    // TODO: grab the name of the book that was clicked on to be updated from state and put in value
                                    value={this.state.title}
                                />
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formDescription">
                                <Form.Label>Des</Form.Label>
                                <Form.Control 
                                    type="name" 
                                    placeholder={this.state.color} 
                                    onChange={this.handleColorChange}
                                    value={this.state.color}
                                />
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formLocation">
                                <Form.Label>Location</Form.Label>
                                <Form.Control 
                                    type="name" 
                                    placeholder={this.state.location} 
                                    onChange={this.handleLocationChange}
                                    value={this.state.location}
                                />
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formClawsCheckbox">
                                <Form.Check 
                                    type="checkbox" 
                                    label="Has claws" 
                                    onChange={this.handleClawChange}
                                    checked={this.state.hasClaws}
                                />
                            </Form.Group>

                            <Button variant="primary" type="submit">
                                Submit
                            </Button>
                        </Form>
                    </Modal.Body>

                    <Modal.Footer>
                        <Button onClick={this.props.onHide} variant="secondary">Close</Button>
                    </Modal.Footer>
                </Modal.Dialog>
            </Modal>
        )
    }
}

export default BookUpdate;
